import { Component } from '@angular/core';
import { Student } from '../models/student.model';

@Component({
  selector: 'app-main',
  templateUrl: './main.component.html',
  styleUrls: ['./main.component.css']
})
interface Language {
  name: string;
  checked: boolean;
}
export class MainComponent {
  // message:string = 'This is a test message';
  // x:number | string = 123;
  //student:Student;
  studentName: string = ""
  studentDOB?: Date
  gender: string = "Male"
  languages: Language[] = [
    {
      name: "English",
      checked: true
    },
    {
      name: "French",
      checked: false
    },

    {
      name: "German",
      checked: false
    },
    {
      name: "Vietnamese",
      checked: false
    },
    {
      name: "Korean",
      checked: false
    }]
  get selectedFruits(): string[] {
    return this.languages.filter(language => language.checked == true)
    .map(fruit => fruit.name);
  }  


  constructor() {
    //this.student =  new Student({name: 'Hoang', age:18});
  }
}
